import React, { useMemo } from "react";
import { useContext, useState } from "react";
import { ThemeManagerContext } from "../../App";
import HomePage from "../../pages/HomePage/HomePage";
import DefaultTheme from "../../pages/ThemeSwitcher/DefaultTheme";
import DystopiaTheme from "../../pages/ThemeSwitcher/DystopiaTheme";
import EightiesTheme from "../../pages/ThemeSwitcher/EightiesTheme";
import SafariTheme from "../../pages/ThemeSwitcher/SafariTheme";
import SpookyTheme from "../../pages/ThemeSwitcher/SpookyTheme";
import ThemeSwitcher from "../../pages/ThemeSwitcher/Themeswitcher";
import ThemeManager, { GlobalThemes } from "../../ThemeManager";
import NavBar from "../NavBar/NavBar";
import { useHistory } from "react-router-dom";

import "./RenderComponent.scss";
export const CustomHistoryContext = React.createContext(undefined);
const RenderComponent = () => {
  const { themeManager, theme, setThemeContext } =
    useContext(ThemeManagerContext);
  const history = useHistory();
  const getPage = useMemo(() => {
    switch (theme) {
      case GlobalThemes.Spooky:
        return <SpookyTheme />;
      case GlobalThemes.Safari:
        return <SafariTheme />;
      case GlobalThemes.Arcade:
        return <EightiesTheme />;
      case GlobalThemes.Dystopia:
        return <DystopiaTheme />;
      default:
        return <DefaultTheme />;
    }
  }, [theme]);
  return (
    <div className={"renderContainer " + theme}>
      <div className="leftContainer">
        <NavBar isVertical={true} />
      </div>
      <div className="rightContainer">{getPage}</div>
    </div>
  );
};
export default RenderComponent;
