import { useContext, useState } from "react";
import { ThemeManagerContext } from "../../App";
import { GlobalThemes } from "../../ThemeManager";
import Icon from "../Icon/Icon";
import { CustomHistoryContext } from "../RenderComponent/RenderComponent";
import "./NavButton.scss";
export interface NavButtonOptions {
  icon?: string;
  label?: string;
  contextItems?: NavButtonOptions[];
  secondary?: boolean;
  themeChanger?: GlobalThemes;
  route?: string;
}
const NavButton = ({ options }: { options: NavButtonOptions }) => {
  const { themeManager, theme, setThemeContext } =
    useContext(ThemeManagerContext);
  const history = useContext(CustomHistoryContext);

  const [isHover, setIsHover] = useState(false);
  const [open, setOpen] = useState<boolean>(false);
  const navigate = () => {
    if (options.route) {
    } else if (options.themeChanger && themeManager) {
      setThemeContext(options.themeChanger);
    }
  };
  var icon = theme === GlobalThemes.Safari ? "Paw" : options.icon;
  return (
    <div className={theme}>
      <div
        className={
          " basicNavButton " +
          (options.secondary === true && " secondary") +
          (open ? " open" : "")
        }
        onMouseEnter={() => setIsHover(true)}
        onMouseLeave={() => setIsHover(false)}
        onClick={() => navigate()}
      >
        {!options.secondary && isHover && theme === GlobalThemes.Spooky && (
          <div className="burst">
            <div className="line">
              <Icon icon={icon} />
            </div>
            <div className="line">
              <Icon icon={icon} />
            </div>
            <div className="line">
              <Icon icon={icon} />
            </div>
            <div className="line">
              <Icon icon={icon} />
            </div>
            <div className="line">
              <Icon icon={icon} />
            </div>
            <div className="line">
              <Icon icon={icon} />
            </div>
          </div>
        )}
        <div className={"navButtonIcon"}>
          {!options.secondary && <Icon icon={options.icon} fontSize={24} />}
        </div>
        <div className={"navButtonLabel"}>{options.label}</div>
        {options.contextItems && (
          <div
            className={"navButtonDropdownArrow " + (open ? "flip" : "")}
            onClick={() => {
              setOpen(!open);
            }}
          >
            <Icon icon="ArrowDropDown" color={theme} />
          </div>
        )}
        {!options.secondary && isHover && theme === GlobalThemes.Safari && (
          <div className="burst">
            <div className="line">
              <Icon icon={icon} />
            </div>
            <div className="line">
              <Icon icon={icon} />
            </div>
            <div className="line">
              <Icon icon={icon} />
            </div>
            <div className="line">
              <Icon icon={icon} />
            </div>
            <div className="line">
              <Icon icon={icon} />
            </div>
            <div className="line">
              <Icon icon={icon} />
            </div>
          </div>
        )}
      </div>
      {open && options.contextItems && (
        <div className={"navButtonContextContainer"}>
          {options.contextItems.map((item, i) => {
            item.secondary = true;
            return <NavButton key={i} options={item} />;
          })}
        </div>
      )}
    </div>
  );
};
export default NavButton;
