import { iDisplay, DisplayElement } from "../ResumePage/ResumePage";
import tacoImage from "../../../assets/taco.jpg";
import hallowImage from "../../../assets/hallow.jpg";
import spaceImage from "../../../assets/spaceBackground.png";

import "./ProjectsPage.scss";
const ProjectsPage = () => {
  const jobList: iDisplay[] = [
    {
      label: "The Asteroid Dome",
      date: "2020 - Present",
      image: spaceImage,
      context: [
        {
          label:
            "A Mobile Online Multiplayer game that puts the player in the pilots seat as they make there way in the galaxy. Buy, Sell & Trade and battle to increase your standing within the galaxy! Fight other players in pitched PvP action!",
        },
      ],
    },
    {
      label: "Tacopocalypse",
      date: "2016",
      link: "https://store.steampowered.com/app/416530/Tacopocalypse/",
      image: tacoImage,
      context: [
        {
          label:
            "Recorded and produced foley sound design for every aspect of the game. These ranged from Tool Sounds for the Customization menu,  ",
        },
      ],
    },
    {
      label: "Hallow",
      date: "2015",
      link: "https://killerham.itch.io/hollow",
      image: hallowImage,
      context: [
        {
          label:
            "A 48 Hour Spooky Game Jam Winner using Leap Motion Controller from UltraLeap. I produced real horse noises by visiting a ranch and recording horses running. I then mixed all the sounds I recorded to produce tree falling sounds, horse running loop, and scary dark horror sounds for our spooky game. It produced a fun, immersive and spooky VR Experience using 3D audio effects within Unreal Engine.",
        },
      ],
    },
  ];
  return (
    <div className="wholeBackground">
      <div className="contentBackground">
        <div className="content">
          <div
            className="titleText 
            titlePrimary"
          >
            Projects
          </div>
          <div className="jobContainer">
            <div className="primaryText titleOne slide1">Unity Projecs</div>
            {jobList.map((job, i) => {
              return <DisplayElement options={job} />;
            })}
          </div>
        </div>
      </div>
    </div>
  );
};
export default ProjectsPage;
