import { Select } from "@material-ui/core";
import { useMemo } from "react";
import { GlobalThemes } from "../../ThemeManager";
import DefaultTheme from "./DefaultTheme";
import DystopiaTheme from "./DystopiaTheme";
import EightiesTheme from "./EightiesTheme";
import SafariTheme from "./SafariTheme";
import SpookyTheme from "./SpookyTheme";
import "./ThemeSwitcher.scss";
const ThemeSwitcher = ({
  theme,
  setTheme,
}: {
  theme: GlobalThemes;
  setTheme: any;
}) => {
  const handleTheme = (event: any) => {
    setTheme(event.target.value);
  };
  const getPage = useMemo(() => {
    switch (theme) {
      case GlobalThemes.Spooky:
        return <SpookyTheme setTheme={setTheme} />;
      case GlobalThemes.Safari:
        return <SafariTheme setTheme={setTheme} />;
      case GlobalThemes.Arcade:
        return <EightiesTheme setTheme={setTheme} />;
      case GlobalThemes.Dystopia:
        return <DystopiaTheme setTheme={setTheme} />;
      default:
        return <DefaultTheme setTheme={setTheme} />;
    }
  }, [theme]);
  return (
    <div className="themeSwitcherContainer">
      <Select native value={theme} onChange={handleTheme}>
        <option value={GlobalThemes.Enterprise}>Default</option>
        <option value={GlobalThemes.Spooky}>Spooky</option>
        <option value={GlobalThemes.Safari}>Safari</option>
        <option value={GlobalThemes.Arcade}>Eighties</option>
        <option value={GlobalThemes.Dystopia}>Dystopia</option>
      </Select>
      {getPage}
    </div>
  );
};
export default ThemeSwitcher;
