import {
  Dispatch,
  SetStateAction,
  useContext,
  useEffect,
  useState,
} from "react";
import { ThemeManagerContext } from "../../App";
import { GlobalThemes } from "../../ThemeManager";
import Icon from "../Icon/Icon";
import { useHistory } from "react-router-dom";
import "./NavButton.scss";
import React from "react";
export interface NavButtonOptions {
  icon?: string;
  label?: string;
  contextItems?: NavButtonOptions[];
  secondary?: boolean;
  themeChanger?: GlobalThemes;
  route?: string;
  parentSetOpen?: Dispatch<SetStateAction<boolean>>;
}
const NavButton = ({ options }: { options: NavButtonOptions }) => {
  const { themeManager, theme, setThemeContext } =
    useContext(ThemeManagerContext);
  const history = useHistory();
  const [isHover, setIsHover] = useState(false);
  const [open, setOpen] = useState<boolean>(false);
  let buttonRef = React.createRef<any>();
  const navigate = () => {
    if (options.themeChanger && themeManager) {
      if (options.parentSetOpen) options.parentSetOpen(false);
      setThemeContext(options.themeChanger);
      history.push(options.route);
    }
  };
  var icon = theme === GlobalThemes.Safari ? "Paw" : options.icon;
  useEffect(() => {
    if (options.contextItems && open === true) {
      var buttonRect = buttonRef.current.getBoundingClientRect();
      var contextElement = document.getElementById("contextRef");
      if (contextElement && buttonRect) {
        contextElement.style.left = (buttonRect.left + 5).toString() + "px";
        contextElement.style.top =
          (buttonRect.top + buttonRect.height).toString() + "px";
      }
    }
  }, [open]);
  return (
    <>
      <div
        ref={buttonRef}
        className={
          " basicNavButton " +
          (options.secondary === true && " secondary") +
          (!open ? "" : " open")
        }
        onMouseEnter={() => setIsHover(true)}
        onMouseLeave={() => setIsHover(false)}
        onClick={() => {
          if (options.contextItems) {
            setOpen(!open);
          } else {
            navigate();
          }
        }}
      >
        {!options.secondary && isHover && theme === GlobalThemes.Spooky && (
          <div className="burst">
            <div className="line">
              <Icon icon={icon} />
            </div>
            <div className="line">
              <Icon icon={icon} />
            </div>
            <div className="line">
              <Icon icon={icon} />
            </div>
            <div className="line">
              <Icon icon={icon} />
            </div>
            <div className="line">
              <Icon icon={icon} />
            </div>
            <div className="line">
              <Icon icon={icon} />
            </div>
          </div>
        )}
        <div className={"navButtonIcon"}>
          {<Icon icon={options.icon} fontSize={16} />}
        </div>
        <div className={"navButtonLabel headline four"}>{options.label}</div>
        {options.contextItems && (
          <div
            className={"navButtonDropdownArrow " + (open ? "flip" : "")}
            onClick={() => {
              setOpen(!open);
            }}
          >
            <Icon icon="ArrowDropDown" color={theme} fontSize={16} />
          </div>
        )}
        {!options.secondary && isHover && theme === GlobalThemes.Safari && (
          <div className="burst">
            <div className="line">
              <Icon icon={icon} />
            </div>
            <div className="line">
              <Icon icon={icon} />
            </div>
            <div className="line">
              <Icon icon={icon} />
            </div>
            <div className="line">
              <Icon icon={icon} />
            </div>
            <div className="line">
              <Icon icon={icon} />
            </div>
            <div className="line">
              <Icon icon={icon} />
            </div>
          </div>
        )}
      </div>
      {open === true && options.contextItems && (
        <div id="contextRef" className={"navButtonContextContainer slide0"}>
          {options.contextItems.map((item, i) => {
            item.secondary = true;
            item.parentSetOpen = setOpen;
            return <NavButton key={i} options={item} />;
          })}
        </div>
      )}
    </>
  );
};
export default NavButton;
