import {
  Dispatch,
  SetStateAction,
  useContext,
  useEffect,
  useState,
} from "react";
import { ThemeManagerContext } from "../../App";
import { GlobalThemes } from "../../ThemeManager";
import Icon from "../Icon/Icon";
import "./NavButton.scss";
import React from "react";
import { useNavigate } from "react-router-dom";
export interface NavButtonOptions {
  icon?: string;
  label?: string;
  contextItems?: NavButtonOptions[];
  secondary?: boolean;
  route?: GlobalThemes;
}
const NavButton = ({
  options,
  openContext,
  setOpenContext,
}: {
  options: NavButtonOptions;
  openContext: boolean;
  setOpenContext: Dispatch<SetStateAction<boolean>>;
}) => {
  const { themeManager, theme, setThemeContext } =
    useContext(ThemeManagerContext);
  const navigate = useNavigate();
  const [isHover, setIsHover] = useState(false);
  let buttonRef = React.createRef<any>();
  const innerNavigate = () => {
    setOpenContext(false);
    navigate("../" + options.route, { replace: true });
  };
  var icon = theme === GlobalThemes.Safari ? "Paw" : options.icon;

  return (
    <>
      <div
        ref={buttonRef}
        className={
          " basicNavButton " +
          (options.secondary === true && " secondary") +
          (openContext && options.contextItems ? " open" : "")
        }
        onMouseEnter={() => setIsHover(true)}
        onMouseLeave={() => setIsHover(false)}
        onClick={() => {
          if (options.contextItems) {
            setOpenContext(!openContext);
          } else {
            innerNavigate();
          }
        }}
      >
        {!options.secondary && isHover && theme === GlobalThemes.Spooky && (
          <div className="burst">
            <div className="sprite">
              <Icon icon={icon} />
            </div>
            <div className="sprite">
              <Icon icon={icon} />
            </div>
            <div className="sprite">
              <Icon icon={icon} />
            </div>
            <div className="sprite">
              <Icon icon={icon} />
            </div>
            <div className="sprite">
              <Icon icon={icon} />
            </div>
            <div className="sprite">
              <Icon icon={icon} />
            </div>
          </div>
        )}
        <div className={"navButtonLabel headline six"}>
          {options.label || options.route}
        </div>
        {options.contextItems && (
          <div
            className={"navButtonDropdownArrow " + (openContext ? "flip" : "")}
            onClick={() => {
              setOpenContext(!openContext);
            }}
          >
            <Icon icon="ArrowDropDown" color={theme} fontSize={16} />
          </div>
        )}
        {!options.secondary && isHover && theme === GlobalThemes.Safari && (
          <div className="burst">
            <div className="sprite">
              <Icon icon={icon} />
            </div>
            <div className="sprite">
              <Icon icon={icon} />
            </div>
            <div className="sprite">
              <Icon icon={icon} />
            </div>
            <div className="sprite">
              <Icon icon={icon} />
            </div>
            <div className="sprite">
              <Icon icon={icon} />
            </div>
            <div className="sprite">
              <Icon icon={icon} />
            </div>
          </div>
        )}
      </div>
      {openContext === true && options.contextItems && (
        <div className={"navButtonContextContainer slide0"}>
          {options.contextItems.map((item, i) => {
            item.secondary = true;
            return (
              <NavButton
                key={i}
                options={item}
                openContext={openContext}
                setOpenContext={setOpenContext}
              />
            );
          })}
        </div>
      )}
    </>
  );
};
export default NavButton;
