import { useContext, useState } from "react";
import { GlobalThemes } from "../../ThemeManager";
import Icon from "../Icon/Icon";
import { ThemeManagerContext } from "../RenderComponent/RenderComponent";
import "./NavButton.scss";
export interface NavButtonOptions {
  icon?: string;
  label?: string;
  contextItems?: NavButtonOptions[];
  secondary?: boolean;
  themeChanger?: GlobalThemes;
  route?: string;
}
const NavButton = ({ options }: { options: NavButtonOptions }) => {
  const { themeManager, theme, setThemeContext } =
    useContext(ThemeManagerContext);

  const [isHover, setIsHover] = useState(false);
  const [open, setOpen] = useState<boolean>(false);
  const navigate = () => {
    if (options.route) {
    } else if (options.themeChanger && themeManager) {
      setThemeContext(options.themeChanger);
    }
  };
  return (
    <div className={theme}>
      <div
        className={" basicNavButton "}
        onMouseEnter={() => setIsHover(true)}
        onMouseLeave={() => setIsHover(false)}
        onClick={() => navigate()}
      >
        {isHover && theme === GlobalThemes.Spooky && (
          <div className="burst">
            {() => {
              for (let i = 0; i < 6; i++) {
                <div className="line">
                  <Icon icon={options.icon} />
                </div>;
              }
            }}
          </div>
        )}

        <div className={"navButtonIcon"}>
          {!options.secondary && <Icon icon={options.icon} />}
        </div>
        <div className={"navButtonLabel"}>{options.label}</div>
        {options.contextItems && (
          <div
            className={"navButtonDropdownArrow"}
            onClick={() => {
              setOpen(!open);
            }}
          >
            <Icon icon="ArrowDropDown" color={theme} />
          </div>
        )}
      </div>

      {open && options.contextItems && (
        <div className={"navButtonContextContainer"}>
          {options.contextItems.map((item, i) => {
            item.secondary = true;
            return <NavButton key={i} options={item} />;
          })}
        </div>
      )}
    </div>
  );
};
export default NavButton;
