import { useEffect, useMemo, useRef } from "react";
import { useContext, useState } from "react";
import { ThemeManagerContext } from "../../App";
import EnterpriseTheme from "../../pages/ThemeSwitcher/EnterpriseTheme/EnterpriseTheme";
import DystopiaTheme from "../../pages/ThemeSwitcher/DystopiaTheme";
import EightiesTheme from "../../pages/ThemeSwitcher/EightiesTheme";
import SafariTheme from "../../pages/ThemeSwitcher/SafariTheme";
import SpookyTheme from "../../pages/ThemeSwitcher/SpookyTheme";
import { GlobalThemes } from "../../ThemeManager";
import NavBar from "../NavBar/NavBar";
import "./RenderComponent.scss";
import ResumePage from "../../pages/ThemeSwitcher/ResumePage/ResumePage";
import OverlayControl from "../OverlayControl/OverlayControl";
import ContactPage from "../../pages/ThemeSwitcher/ContactPage/ContactPage";
import ProjectsPage from "../../pages/ThemeSwitcher/ProjectsPage/ProjectsPage";
import BezierBackground from "../BezierBackground/Bezier-Background";
import SideBar from "../SideBar/SideBar";
import SalesTheme from "../../pages/ThemeSwitcher/SalesTheme/SalesTheme";
import DesignBackground from "../BezierBackground/DesignBackground";
import TestPage from "../../pages/testPage";
const RenderComponent = () => {
  let isTopBar = true;
  const { themeManager, theme, setThemeContext } =
    useContext(ThemeManagerContext);
  const [page, setPage] = useState<JSX.Element>(<></>);
  const [switchButton, setSwitchButton] = useState<Boolean>(false);
  useEffect(() => {
    switch (theme) {
      case GlobalThemes.Projects:
        setPage(<ProjectsPage />);
        break;
      case GlobalThemes.Contact:
        setPage(<ContactPage />);
        break;
      case GlobalThemes.Spooky:
        setPage(<SpookyTheme />);
        break;
      case GlobalThemes.Safari:
        setPage(<SafariTheme />);
        break;
      case GlobalThemes.Arcade:
        setPage(<EightiesTheme />);
        break;
      case GlobalThemes.Dystopia:
        setPage(<DystopiaTheme />);
        break;
      case GlobalThemes.Enterprise:
        setPage(<EnterpriseTheme />);
        break;
      case GlobalThemes.Sales:
        setPage(<SalesTheme />);
        break;
      case GlobalThemes.Test:
        setPage(<TestPage />);
        break;
      case GlobalThemes.Resume:
      default:
        setPage(<ResumePage />);
        break;
    }
  }, [theme]);
  const showBackground = useMemo(() => {
    switch (theme) {
      case GlobalThemes.Resume:
      case GlobalThemes.Contact:
      case GlobalThemes.Projects:
        return false;
      default:
        return false;
    }
  }, [theme]);
  const showSideBar = useMemo(() => {
    switch (theme) {
      case GlobalThemes.Resume:
      case GlobalThemes.Contact:
      case GlobalThemes.Projects:
      case GlobalThemes.Spooky:
      case GlobalThemes.Sales:
      case GlobalThemes.Test:
        return false;
      default:
        return true;
    }
  }, [theme]);
  return (
    <>
      {showBackground && <DesignBackground />}
      <div id="animationOverlay" className="animationOverlay"></div>
      <div
        className={"renderContainer " + theme + (isTopBar ? " horizontal" : "")}
      >
        {theme !== GlobalThemes.Contact && (
          <button
            className={
              "button secondary contactButton " +
              (theme !== GlobalThemes.Resume
                ? " active"
                : switchButton
                ? " active"
                : "")
            }
            onClick={() => {
              setThemeContext(GlobalThemes.Contact);
            }}
          >
            Contact
          </button>
        )}
        <div className="topContainer">
          <NavBar isTopBar={isTopBar} />
        </div>
        <div className="bottomContainer">
          {showSideBar && <SideBar />}
          <CalculatedScrollComponent hasButtons={false} refresh={[]}>
            <div
              className={"mainContent " + theme}
              onScroll={(e) => {
                setSwitchButton((e.target as any).scrollTop > 0);
              }}
            >
              {!page ? <OverlayControl loading={true} /> : page}
            </div>
          </CalculatedScrollComponent>
        </div>
      </div>
    </>
  );
};
export default RenderComponent;
export const CalculatedScrollComponent = (props: {
  children: any;
  hasButtons: boolean;
  refresh: any[];
  overflowHidden?: boolean;
}) => {
  const resizeRef = useRef<any>(null);
  const buttonSpacing = useRef<number>(0);
  const initialResize = (firstCall?: boolean) => {
    if (resizeRef.current) {
      resizeChildrenContainer();
      if (firstCall) {
        setTimeout(() => {
          initialResize();
        }, 500);
      }
    } else {
      setTimeout(() => {
        initialResize();
      }, 500);
    }
  };
  useEffect(() => {
    window.addEventListener("resize", resizeChildrenContainer);
    initialResize(true);
    return () => {
      window.removeEventListener("resize", resizeChildrenContainer);
    };
  }, []);
  useEffect(() => {
    buttonSpacing.current = props.hasButtons ? 72 : 0;
  }, [props.hasButtons]);
  useEffect(() => {
    resizeChildrenContainer();
  }, [resizeRef, ...props.refresh, buttonSpacing.current]);
  const resizeChildrenContainer = () => {
    if (resizeRef.current) {
      const resizeContainerRect = resizeRef.current.getBoundingClientRect();
      const calcHeight =
        window.innerHeight - resizeContainerRect.y - 0 - buttonSpacing.current;
      resizeRef.current.style.height = calcHeight.toString() + "px";
    }
  };
  return (
    <div
      ref={resizeRef}
      id="scrollComponent"
      className="scrollComponent"
      style={{
        overflowY: props.overflowHidden ? "hidden" : "auto",
        overflowX: "hidden",
        width: "100%",
        position: "relative",
        display: "flex",
      }}
    >
      {props.children}
    </div>
  );
};
