import { useContext, useMemo, useState } from "react";
import { GlobalThemes } from "../../ThemeManager";
import NavButton, { NavButtonOptions } from "../NavButton/NavButton";
import { ThemeManagerContext } from "../RenderComponent/RenderComponent";
import "./NavBar.scss";
const NavBar = ({ isVertical }: { isVertical?: boolean }) => {
  const themeManager = useContext(ThemeManagerContext);
  const spookyNavButtons: string[] = ["Skull", "Bat", "Pumpkin"];
  const safariNavButtons: string[] = ["Sun", "Bug", "Paw"];
  const eightiesNavButtons: string[] = ["Atari", "SportsEsports", "Tama"];
  const dystopiaNavButtons: string[] = ["Atari", "SportsEsports", "Tama"];
  const defaultNavButtons: string[] = ["Home", "Themes", "Projects"];
  const [label, setLabel] = useState("Webpage");
  const [navButtons, setNavButtons] = useState<NavButtonOptions[]>([
    { icon: "Home", label: "Home" },
    {
      icon: "Themes",
      label: "Themes Page",
      contextItems: [
        { icon: "Projects", label: "Enterprise" },
        { icon: "Bat", label: "Haunted House" },
        { icon: "Paw", label: "Safari Learning" },
        { icon: "SportsEsports", label: "80s Arcade" },
        { icon: "Skull", label: "Dystopia" },
      ],
    },
    { icon: "Projects", label: "Projects" },
  ]);
  const setIcons = (icons: string[]) => {
    let tempButtons = navButtons;
    icons.forEach((icon, i) => {
      tempButtons[i]["icon"] = icon;
    });
    setNavButtons(tempButtons);
  };
  const getTheme = useMemo(() => {
    switch (themeManager?.themeName) {
      case GlobalThemes.Spooky:
        setLabel("Haunted House Tours");
        setIcons(spookyNavButtons);
        return "spooky";
      case GlobalThemes.Safari:
        setLabel("Safari Learning Center");
        setIcons(safariNavButtons);
        return "safari";
      case GlobalThemes.Arcade:
        setLabel("Eighties Arcade");
        setIcons(eightiesNavButtons);
        return "eighties";
      case GlobalThemes.Dystopia:
        setLabel("Dystopia");
        setIcons(dystopiaNavButtons);
        return "dystopia";
      default:
        setLabel("Enterprise");
        setIcons(defaultNavButtons);
        return "default";
    }
  }, [themeManager?.themeName]);

  return (
    <div
      className={
        "navBarContainer " +
        (isVertical ? "vertical " : "horizontal ") +
        getTheme
      }
    >
      <div className="navBarLabel">{label}</div>
      {navButtons.map((y) => {
        return <NavButton options={y} />;
      })}
    </div>
  );
};
export default NavBar;
