import { useEffect, useMemo } from "react";
import { useContext, useState } from "react";
import { ThemeManagerContext } from "../../App";
import EnterpriseTheme from "../../pages/ThemeSwitcher/DefaultTheme";
import DystopiaTheme from "../../pages/ThemeSwitcher/DystopiaTheme";
import EightiesTheme from "../../pages/ThemeSwitcher/EightiesTheme";
import SafariTheme from "../../pages/ThemeSwitcher/SafariTheme";
import SpookyTheme from "../../pages/ThemeSwitcher/SpookyTheme";
import { GlobalThemes } from "../../ThemeManager";
import NavBar from "../NavBar/NavBar";

import "./RenderComponent.scss";
import ResumePage from "../../pages/ThemeSwitcher/ResumePage/ResumePage";
import OverlayControl from "../OverlayControl/OverlayControl";
import ContactPage from "../../pages/ThemeSwitcher/ContactPage/ContactPage";
import backgroundImage from "../../assets/wallpaper.png";
import ProjectsPage from "../../pages/ThemeSwitcher/ProjectsPage/ProjectsPage";
import BezierBackground from "../BezierBackground/Bezier-Background";
import SideBar from "../SideBar/SideBar";
const RenderComponent = () => {
  let isTopBar = true;
  const { themeManager, theme, setThemeContext } =
    useContext(ThemeManagerContext);
  const [page, setPage] = useState<JSX.Element>(<></>);
  useEffect(() => {
    switch (theme) {
      case GlobalThemes.Projects:
        setPage(<ProjectsPage />);
        break;
      case GlobalThemes.Contact:
        setPage(<ContactPage />);
        break;
      case GlobalThemes.Spooky:
        setPage(<SpookyTheme />);
        break;
      case GlobalThemes.Safari:
        setPage(<SafariTheme />);
        break;
      case GlobalThemes.Arcade:
        setPage(<EightiesTheme />);
        break;
      case GlobalThemes.Dystopia:
        setPage(<DystopiaTheme />);
        break;
      case GlobalThemes.Enterprise:
        setPage(<EnterpriseTheme />);
        break;
      case GlobalThemes.Resume:
      default:
        setPage(<ResumePage />);
        break;
    }
  }, [theme]);
  const showBackground = useMemo(() => {
    switch (theme) {
      case GlobalThemes.Resume:
      case GlobalThemes.Contact:
      case GlobalThemes.Projects:
        return true;
      default:
        return false;
    }
  }, [theme]);
  const showSideBar = useMemo(() => {
    switch (theme) {
      case GlobalThemes.Resume:
      case GlobalThemes.Contact:
      case GlobalThemes.Projects:
        return false;
      default:
        return true;
    }
  }, [theme]);
  return (
    <>
      {showBackground && (
        /*<img
          style={{
            position: "fixed",
            zIndex: -1,
          }}
          src={backgroundImage}
        />*/
        <BezierBackground />
      )}
      <div
        className={"renderContainer " + theme + (isTopBar ? " horizontal" : "")}
      >
        <div className="topContainer">
          <NavBar isTopBar={isTopBar} />
        </div>
        <div className="bottomContainer">
          {showSideBar && <SideBar />}
          <div className="mainContent">
            {!page ? <OverlayControl loading={true} /> : page}
          </div>
        </div>
      </div>
    </>
  );
};
export default RenderComponent;
