import { useEffect, useState } from "react";
function getWindowDimensions() {
  const { innerWidth: width, innerHeight: height } = window;
  return {
    width,
    height,
  };
}

export function useWindowDimensions() {
  const [windowDimensions, setWindowDimensions] = useState(
    getWindowDimensions()
  );

  useEffect(() => {
    function handleResize() {
      setWindowDimensions(getWindowDimensions());
    }

    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);
  return windowDimensions;
}
export interface componentSchema {
  primaryColor: string;
  secondaryColor: string;
  teritaryColor: string;
  borderColor: string;
  titleText: string;
  primaryText: string;
  secondaryText: string;
  placeholderText: string;
  error: string;
  primaryBackground: string;
  secondaryBackground: string;
  primaryTransparentBackground: string;
  secondaryTransparentBackground: string;
  successButton: string;
  backButton: string;
  optionButton: string;
  switchOn: string;
  switchOff: string;
  fontXSmall: number;
  fontSmall: number;
  fontMedium: number;
  fontLarge: number;
  fontXLarge: number;
  radiusSmall: number;
  radiusMedium: number;
  radiusLarge: number;
  whiteBackground: string;
  shadow: string;
}
export interface colorSchema {
  blueDark: string;
  blueNormal: string;
  blueLight: string;
  redDarker: string;
  redDark: string;
  redNormal: string;
  redLight: string;
  redLighter: string;
  yellowDark: string;
  yellowNormal: string;
  yellowLight: string;
  orangeDark: string;
  orangeMedium: string;
  orangeLight: string;
  greenDark: string;
  greenNormal: string;
  greenLight: string;
  greenLighter: string;
  darkDark: string;
  darkNormal: string;
  darkLight: string;
  lightDark: string;
  lightNormal: string;
  lightLight: string;
  purpleDark: string;
  purpleNormal: string;
  purpleLight: string;
  background: string;
  logoLight: string;
  logoDark: string;
  backgroundTransparent: string;
  secondaryBackgroundTransparent: string;
  fontXSmall: number;
  fontSmall: number;
  fontMedium: number;
  fontLarge: number;
  fontXLarge: number;
  radiusSmall: number;
  radiusMedium: number;
  radiusLarge: number;
  shadow: string;
  borderLight: string;
}
export enum GlobalThemes {
  Light = "Light",
  Dark = "Dark",
  Safari = "Safari",
  Spooky = "Spooky",
  Dystopia = "Dystopia",
  Enterprise = "Enterprise",
  Arcade = "Arcade",
}
class ThemeManager {
  isMobile: boolean;
  phoneHeight: number;
  phoneWidth: number;
  currentTheme: componentSchema;
  globalComponentThemes: Map<string, componentSchema>;
  globalStyles: any;
  themeName: GlobalThemes;
  globalColors: colorSchema = {
    blueDark: "#3568d4",
    blueNormal: "#3e7bfa",
    blueLight: "#6698fa",
    redDarker: "#4f0c0f",
    redDark: "#81151a",
    redNormal: "#cc1b24",
    redLight: "#e8666d",
    redLighter: "#edbbbe",
    yellowDark: "#ffcc00",
    yellowNormal: "#fddd48",
    yellowLight: "#fded72",
    greenDark: "#06c270",
    greenNormal: "#39d98a",
    greenLight: "#57eba1",
    greenLighter: "#b5fbd8",
    orangeDark: "#bf7008",
    orangeMedium: "#fc9003",
    orangeLight: "#f7ae4d",
    darkDark: "#1C1C26",
    darkNormal: "#262637",
    darkLight: "#8f90a6",
    borderLight: "#cccccc",
    lightDark: "#ebebf0",
    lightNormal: "#f2f2f5",
    logoDark: "#05A660",
    logoLight: "#3CE792",
    lightLight: "#fafafc",
    purpleDark: "#613269",
    purpleNormal: "#9d59a8",
    purpleLight: "#de91eb",
    background: "#FFFFFF",
    backgroundTransparent: "rgba(255, 255, 255, 0.7)",
    secondaryBackgroundTransparent: "rgba(222, 222, 255, 0.7)",
    fontXSmall: 10,
    fontSmall: 15,
    fontMedium: 20,
    fontLarge: 25,
    fontXLarge: 30,
    radiusSmall: 5,
    radiusMedium: 10,
    radiusLarge: 15,
    shadow:
      "0px 0px 2px rgba(40, 41, 61, 0.04), 0px 4px 8px rgba(96, 97, 112, 0.16);",
  };
  dystopiaComponentTheme: componentSchema = {
    borderColor: this.globalColors.darkNormal,
    primaryBackground: this.globalColors.redLighter,
    secondaryBackground: this.globalColors.redDark,
    primaryTransparentBackground: this.globalColors.backgroundTransparent,
    secondaryTransparentBackground:
      this.globalColors.secondaryBackgroundTransparent,
    primaryColor: this.globalColors.redNormal,
    secondaryColor: this.globalColors.redDark,
    teritaryColor: this.globalColors.redLight,
    titleText: this.globalColors.redLight,
    primaryText: this.globalColors.lightNormal,
    secondaryText: this.globalColors.lightDark,
    placeholderText: this.globalColors.lightLight,

    error: this.globalColors.yellowNormal,
    successButton: this.globalColors.redLight,
    backButton: this.globalColors.redLight,
    optionButton: this.globalColors.orangeMedium,
    switchOn: this.globalColors.orangeLight,
    switchOff: this.globalColors.redDark,
    whiteBackground: "#ffffff",
    shadow: this.globalColors.shadow,

    fontSmall: this.globalColors.fontSmall,
    fontMedium: this.globalColors.fontMedium,
    fontLarge: this.globalColors.fontLarge,
    fontXSmall: this.globalColors.fontXSmall,
    fontXLarge: this.globalColors.fontXLarge,
    radiusLarge: this.globalColors.radiusLarge,
    radiusMedium: this.globalColors.radiusMedium,
    radiusSmall: this.globalColors.radiusSmall,
  };
  constructor() {
    this.globalComponentThemes = new Map<string, componentSchema>().set(
      GlobalThemes.Dystopia,
      this.dystopiaComponentTheme
    );
    this.phoneHeight = window.innerHeight;
    this.phoneWidth = window.innerWidth;
    this.isMobile = this.checkMobile();
    this.currentTheme = this.dystopiaComponentTheme; //This is to silence needing an initializer
    this.themeName = GlobalThemes.Light;
    this.setTheme(GlobalThemes.Light); /// This is where you set the Theme
  }
  checkMobile = () => {
    if (typeof document != "undefined") {
      return false;
    } else if (
      typeof navigator != "undefined" &&
      navigator.product == "ReactNative"
    ) {
      return true;
    }
    return false;
  };
  setTheme = (theme: GlobalThemes) => {
    this.themeName = theme;
    this.currentTheme =
      this.globalComponentThemes.get(theme) ?? this.dystopiaComponentTheme;
    if (!this.isMobile) {
      document.body.classList.remove(
        GlobalThemes.Enterprise,
        GlobalThemes.Spooky,
        GlobalThemes.Safari,
        GlobalThemes.Arcade,
        GlobalThemes.Dystopia
      );
      document.body.classList.add(theme);
    }
    var sheet = window.document.styleSheets[0];
    sheet.insertRule(
      ":root { " + this.getCssTheme() + " }",
      sheet.cssRules.length
    );
  };
  getCssTheme = () => {
    return (
      `--theme: ` +
      this.themeName.toString() +
      `;
    --theme-primary:` +
      this.currentTheme.primaryColor +
      ";" +
      `--theme-secondary:` +
      this.currentTheme.secondaryColor +
      ";" +
      `--theme-teritary:` +
      this.currentTheme.teritaryColor +
      ";" +
      `--theme-border:` +
      this.currentTheme.borderColor +
      ";" +
      `--theme-text-title:` +
      this.currentTheme.titleText +
      ";" +
      `--theme-text-primary:` +
      this.currentTheme.primaryText +
      ";" +
      `--theme-text-secondary:` +
      this.currentTheme.secondaryText +
      ";" +
      `--theme-text-placeholder:` +
      this.currentTheme.placeholderText +
      ";" +
      `--theme-text-error:` +
      this.currentTheme.error +
      ";" +
      `--theme-primary-background:` +
      this.currentTheme.primaryBackground +
      ";" +
      `--theme-secondary-background:` +
      this.currentTheme.secondaryBackground +
      ";" +
      `--theme-primary-background-transparent:` +
      this.currentTheme.primaryTransparentBackground +
      ";" +
      `--theme-secondary-background-transparent: ` +
      this.currentTheme.secondaryTransparentBackground +
      ";" +
      `--theme-button-success:` +
      this.currentTheme.successButton +
      ";" +
      `--theme-button-back:` +
      this.currentTheme.backButton +
      ";" +
      `--theme-button-option:` +
      this.currentTheme.optionButton +
      ";" +
      `--theme-white-background:` +
      this.currentTheme.whiteBackground +
      ";" +
      `--theme-shadow:` +
      this.currentTheme.shadow +
      ";"
    );
  };
}
export default ThemeManager;
