import { TextField, Button } from "@material-ui/core";
import { useState } from "react";
import ParallaxBackground from "../../../components/BezierBackground/ParallaxBackground";
import "./ContactPage.scss";
const ContactPage = () => {
  const [values, setValues] = useState({
    firstName: "",
    lastName: "",
    email: "",
    message: "",
  });
  const handleChange = (prop: any) => (event: any) => {
    setValues({ ...values, [prop]: event.target.value });
  };
  const handleSubmit = () => {};
  return (
    <>
      <ParallaxBackground />
      <div className="centerResumeContainer">
        <div className="textPrimary headline one">Contact</div>
        <div className="contactContainer">
          <div className="inputContainer">
            <div className="textInput slideOne">
              <TextField
                label="First Name:"
                value={values.firstName}
                onChange={handleChange("firstName")}
                InputLabelProps={{
                  style: {
                    color: "var(--theme-text-placeholder)",
                  },
                }}
                variant={"filled"}
                fullWidth={true}
              />
            </div>
            <div className="textInput slideTwo">
              <TextField
                label="Last Name:"
                value={values.lastName}
                onChange={handleChange("lastName")}
                variant={"filled"}
                InputLabelProps={{
                  style: {
                    color: "var(--theme-text-placeholder)",
                  },
                }}
                fullWidth={true}
              />
            </div>
          </div>
          <div className="textInput slideThree">
            <TextField
              label="Email Address:"
              value={values.email}
              onChange={handleChange("email")}
              variant={"filled"}
              InputLabelProps={{
                style: {
                  color: "var(--theme-text-placeholder)",
                },
              }}
              fullWidth={true}
            />
          </div>
          <div className="textInput slideFour">
            <TextField
              label="Brief Message:"
              value={values.message}
              onChange={handleChange("message")}
              multiline={true}
              minRows={3}
              variant={"filled"}
              fullWidth={true}
              InputLabelProps={{
                style: {
                  color: "var(--theme-text-placeholder)",
                },
              }}
            />
          </div>
          <button
            className="button secondary slideFive"
            onClick={() => handleSubmit()}
          >
            Submit
          </button>
        </div>
      </div>
    </>
  );
};
export default ContactPage;
