import { useMemo, useState } from 'react';
import NavButton, { NavButtonOptions } from '../NavButton/NavButton';
import { Themes } from '../RenderComponent/RenderComponent';
import './NavBar.scss';
const NavBar = ({isVertical, theme, setTheme}:{isVertical?: boolean, theme: Themes, setTheme:any}) => {
    const spookyNavButtons: string[] = ['Skull', 'Bat', 'Pumpkin'];
    const safariNavButtons: string[] = ['Skull', 'Bat', 'Pumpkin'];
    const eightiesNavButtons: string[] = ['Skull', 'Bat', 'Pumpkin'];
    const defaultNavButtons: string[] = ['Home', 'Themes', 'Projects'];
    const [label, setLabel] = useState('Webpage');
    const [navButtons, setNavButtons] = useState<NavButtonOptions[]>([
        {icon: 'Home', label: 'Home'},
        {icon: 'Themes', label: 'Themes Page'},
        {icon: 'Projects', label: 'Projects'},
    ]);
    const setIcons = (icons: string[]) => {
        let tempButtons = navButtons;
        icons.forEach((icon, i)=>{
            tempButtons[i]['icon'] = icon;
        });
        setNavButtons(tempButtons);
    }
    const getTheme = useMemo(()=>{
        switch(theme){
            case Themes.spooky:
                setLabel('Spooky Theme');
                setIcons(spookyNavButtons);
                return 'spooky'
            case Themes.safari:
                setLabel('Safari Theme');
                setIcons(safariNavButtons);
                return 'safari'
            case Themes.eighties:
                setLabel('Eighties Theme');
                setIcons(eightiesNavButtons);
                return 'eighties'
            default:
                setLabel('Webpage');
                setIcons(defaultNavButtons);
                return 'default'
        }
    },[theme]);
    
    return (
        <div className={'navBarContainer ' + (isVertical ? 'vertical ' : 'horizontal ') + (getTheme)} >
            <div className='navBarLabel'>{label}</div>
            {navButtons.map(y=>{
                return <NavButton options={y}/>
            })}
        </div>
    )
}
export default NavBar;
