import { useContext, useMemo, useState } from "react";
import { ThemeManagerContext } from "../../App";
import { GlobalThemes } from "../../ThemeManager";
import NavButton, { NavButtonOptions } from "../NavButton/NavButton";
import "./NavBar.scss";
const NavBar = ({ isTopBar }: { isTopBar?: boolean }) => {
  const { themeManager, theme, setThemeContext } =
    useContext(ThemeManagerContext);
  const spookyNavButtons: string[] = ["Skull", "Bat", "Pumpkin"];
  const safariNavButtons: string[] = ["Sun", "Bug", "Paw"];
  const eightiesNavButtons: string[] = ["Atari", "Invader", "Tama"];
  const dystopiaNavButtons: string[] = ["Atari", "SportsEsports", "Tama"];
  const defaultNavButtons: string[] = ["Home", "Themes", "Projects"];
  const [label, setLabel] = useState("Webpage");
  const [navButtons, setNavButtons] = useState<NavButtonOptions[]>([
    {
      icon: "Resume",
      label: "Resume",
      themeChanger: GlobalThemes.Resume,
      route: "/resume",
    },

    {
      icon: "Themes",
      label: "Themes Page",
      contextItems: [
        {
          icon: "Projects",
          label: "Enterprise",
          themeChanger: GlobalThemes.Enterprise,
          route: "/enterprise",
        },
        {
          icon: "Bat",
          label: "Haunted House",
          themeChanger: GlobalThemes.Spooky,
          route: "/hauntedHouse",
        },
        {
          icon: "Paw",
          label: "Safari Learning",
          themeChanger: GlobalThemes.Safari,
          route: "/safari",
        },
        {
          icon: "SportsEsports",
          label: "80s Arcade",
          themeChanger: GlobalThemes.Arcade,
          route: "/arcade",
        },
        {
          icon: "Skull",
          label: "Dystopia",
          themeChanger: GlobalThemes.Dystopia,
          route: "/dystopia",
        },
      ],
    },
    {
      icon: "Projects",
      label: "Projects",
      themeChanger: GlobalThemes.Projects,
      route: "/projects",
    },
    {
      icon: "Contact",
      label: "Contact Me",
      themeChanger: GlobalThemes.Contact,
      route: "/contact",
    },
  ]);
  const setIcons = (icons: string[]) => {
    let tempButtons = navButtons;
    icons.forEach((icon, i) => {
      tempButtons[i]["icon"] = icon;
    });
    setNavButtons(tempButtons);
  };
  const getTheme = useMemo(() => {
    switch (theme) {
      case GlobalThemes.Spooky:
        setLabel("Haunted House Tours");
        setIcons(spookyNavButtons);
        break;
      case GlobalThemes.Safari:
        setLabel("Safari Learning Center");
        setIcons(safariNavButtons);
        break;
      case GlobalThemes.Arcade:
        setLabel("80s Arcade");
        setIcons(eightiesNavButtons);
        break;
      case GlobalThemes.Dystopia:
        setLabel("Dystopia");
        setIcons(dystopiaNavButtons);
        break;
      case GlobalThemes.Contact:
      case GlobalThemes.Projects:
      case GlobalThemes.Resume:
      case GlobalThemes.Enterprise:
      default:
        setLabel(theme);
        setIcons(defaultNavButtons);
        break;
    }
    return theme;
  }, [theme]);

  return (
    <div
      className={isTopBar ? "horizontal " : "vertical "}
      style={{
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
      }}
    >
      <div
        className="headline one navBarLabel textTitle"
        style={{ display: "flex" }}
      >
        {"Portfolio - "}
        <div
          style={{ marginLeft: "8px", marginBottom: "2px" }}
          className="textPrimary headline one"
        >
          {theme}
        </div>
      </div>
      <div
        className={
          "navBarContainer " +
          (isTopBar ? "horizontal " : "vertical ") +
          getTheme
        }
      >
        {navButtons.map((y) => {
          return <NavButton options={y} />;
        })}
      </div>
    </div>
  );
};
export default NavBar;
