import "./ResumePage.scss";

export interface iDisplay {
  label: string;
  date?: string;
  image?: string;
  link?: string;
  context?: string[];
  isBulletContext?: boolean;
}
const ResumePage = () => {
  const languagesList: iDisplay[] = [
    {
      label: "Languages",
      isBulletContext: true,
      context: [
        "HTML5",
        "SCSS",
        "JavaScript",
        "TypeScript",
        "C#",
        "KefirJS",
        "",
        "Extra Language",
      ],
    },
  ];
  const jobList: iDisplay[] = [
    {
      label: "StatusQuote Inc",
      date: "January 2020 - Present",
      context: [
        "Constructed a React Native and React Solution that could run both simultaneously. Then split the Logic Codebase from the Rendering Codebase of each, allowing the Logic codebase to work for all three platforms! This created a highly modular and efficent Codebase for each app.",
        "Developed the UI/UX Experience for a Enterprise-Level Investment app built in React Native and React. Displayed detailed Chart Controls and easily digested data for our Investment Clients.",
        "Exchanged communications with many top level clients, such as the Head of a Global Medical Company, to accurately create enterprise level in-house financial applications and organizational software.",
        "Created programs that organize financial data into Government Regulated Excel and PDF documents to be submitted easily. Simplifying a meticulous week long process, to only a click of a button.",
        "Worked on a multitude of projects ranging from React, Angular, .Net MVC, React Native, and many older in-house systems. I adapted quickly to new languages, new techniques and new enviornments to be successful.",
      ],
    },
    {
      label: "GhoulCode",
      date: "2019 - Present",
      context: [
        "Enhanced the user experience of the website, adding icons and increasing flow of the commerce page.",
      ],
    },
    {
      label: "ABC Fine Wine & Spirits",
      date: "2017 - 2019",
      context: [
        "Enhanced the user experience of the website, adding icons and increasing flow of the commerce page.",
      ],
    },
    {
      label: "Falcon of the Nile Water Solutions",
      date: "2008 - 2017",
      context: [
        "Enhanced the user experience of the website, adding icons and increasing flow of the commerce page.",
      ],
    },
  ];
  return (
    <>
      <div className="wholeBackground">
        <div className="contentBackground">
          <div className="content">
            <div
              className="titleText 
            headline one"
            >
              Resume
            </div>
            <div className="displayContainer">
              <div className="primaryText headline two slideOne">Skills</div>
              {/*<div className="line" />*/}
              {languagesList.map((job, i) => {
                return <DisplayElement options={job} />;
              })}
            </div>
            <div className="displayContainer">
              <div className="primaryText headline two slideOne">
                Work History
              </div>
              {/*<div className="line" />*/}
              {jobList.map((job, i) => {
                return <DisplayElement options={job} />;
              })}
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
export default ResumePage;
export const DisplayElement = ({ options }: { options: iDisplay }) => {
  return (
    <div
      style={{ display: "flex", paddingBottom: 24 }}
      className={options.link ? "link" : ""}
      onClick={() => {
        if (options.link) {
          window.open(options.link, "_blank");
        }
      }}
    >
      <div className="jobElementContainer ">
        <div className={"secondaryText headline three slideTwo"}>
          {options.label}
        </div>
        <div
          style={{ fontSize: 16 }}
          className="primaryText headline four slideTwo"
        >
          {options.date ?? ""}
        </div>
        {options.context && (
          <ul
            className={
              options.context.length > 6 ? "gridContext" : "fullContext"
            }
          >
            {options.context.map((job, i) => {
              return (
                <li
                  className="slideThree"
                  style={{
                    animationDelay: (0.5 + 0.25 * (i + 1)).toString() + "s",
                    marginBottom: options.isBulletContext ? "4px" : "16px",
                  }}
                  key={i}
                >
                  <div className={"teritaryText "}>{job}</div>
                </li>
              );
            })}
          </ul>
        )}
      </div>
      {options.image && (
        <img className="jobImage slideTwo" src={options.image} />
      )}
    </div>
  );
};
