import { Component } from "react";
import Chart from "react-google-charts";
import { GlobalThemes } from "../../../ThemeManager";
import stockProfile from "../../../assets/stockProfile.jpeg";
import news1 from "../../../assets/news1.jpg";
import news2 from "../../../assets/news2.jpg";
import news3 from "../../../assets/news3.png";
import "./EnterpriseTheme.scss";
import TutorialControl from "../../../components/TutorialComponent/TutorialControl";
enum PercentageElementStates {
  Decrease,
  Increase,
  None,
}
interface iInvestmentCard {
  label: string;
  fullLabel: string;
  tradingAt: number;
  percentChange: number;
}
const investmentCardList: iInvestmentCard[] = [
  {
    label: "TSLA",
    fullLabel: "Tesla Motors, Inc",
    tradingAt: 246.51,
    percentChange: -1.23,
  },
  {
    label: "FB",
    fullLabel: "Facebook, Inc",
    tradingAt: 189.83,
    percentChange: 2.83,
  },
  {
    label: "AMZN",
    fullLabel: "Amazon",
    tradingAt: 220.93,
    percentChange: 0.23,
  },
];
const EnterpriseTheme = () => {
  const getPercentSymbolValue = (value: number) => {
    let symbol = "";
    if (value > 0) {
      symbol = "+";
    } else if (value < 0) {
      symbol = "-";
    }
    return symbol + value + "%";
  };
  const investmentCardElement = (props: iInvestmentCard) => (
    <div className="investmentCard">
      <div className="headline two">{props.label}</div>
      <div className={"headline four "}>{getPercentSymbolValue(value)}</div>
      <div className="headline three textSecondary">
        From last {accountChanged}
      </div>
    </div>
  );
  return (
    <>
      <div className="personalContainer">
        <div className="card profileCard Right">
          <div className="headline two">Personal Info</div>
          <div className="headline three textTitle">Jacob Jacobson</div>
          <div className="profileContent">
            <div className="profilePic">
              <img src={stockProfile} />
            </div>
            <div className="profileInfo">
              <div className="headline four textPrimary">
                Software Developer
              </div>
              <div className="headline six">
                Their mission is to be the most ethusatic developer around.
                Always challenging themselves to be the best and work on the
                next greatest technology to hown their skills.
              </div>
              <div
                style={{
                  marginTop: "24px",
                  display: "grid",
                  gridTemplateColumns: "auto auto",
                  columnGap: "8px",
                  gridTemplateRows: "auto auto",
                }}
              >
                <div className="headline seven textSecondary">DOB: </div>
                <div className="headline seven textTeritary">06/19/94</div>
                <div className="headline seven textSecondary">SCHOOL: </div>
                <div className="headline seven textTeritary">
                  East County High School
                </div>
                <div className="headline seven textSecondary">UNIVERSITY: </div>
                <div className="headline seven textTeritary">
                  Computer Science at University of Central Ohio
                </div>
                <div className="headline seven textSecondary">FROM: </div>
                <div className="headline seven textTeritary">
                  Portland, Oregon
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="card Left">
          <div className="headline two">News Info</div>
          <div className="cardScrollContent">
            {newsArray.map((news) => {
              return (
                <div className="newsContent">
                  <img className="newsImg" src={news.image} />
                  <div className="flex column">
                    <div className="headline four">{news.label}</div>
                    <div className="headline six">{news.bodyText}</div>
                  </div>
                </div>
              );
            })}
          </div>
          <div className="headline five">More</div>
        </div>
      </div>
      <div className="cardContainer">
        <div className={"card Down chart"}>
          <div className="headline two">Bar Chart</div>

          <Chart
            chartType="BarChart"
            loader={<div>Loading Chart</div>}
            data={[
              ["City", "2010 Population", "2000 Population"],
              ["New York City, NY", 8175000, 8008000],
              ["Los Angeles, CA", 3792000, 3694000],
              ["Chicago, IL", 2695000, 2896000],
              ["Houston, TX", 2099000, 1953000],
              ["Philadelphia, PA", 1526000, 1517000],
            ]}
            options={{
              title: "Population of Largest U.S. Cities",
              chartArea: { width: "50%" },
              isStacked: true,
              hAxis: {
                title: "Total Population",
                minValue: 0,
              },
              vAxis: {
                title: "City",
              },
            }}
            // For tests
            rootProps={{ "data-testid": "3" }}
          />
        </div>
        <div className="card Left chart">
          <div className="headline two">Line Chart</div>

          <Chart
            chartType="LineChart"
            loader={<div>Loading Chart</div>}
            data={[
              ["x", "dogs", "cats"],
              [0, 0, 0],
              [1, 10, 5],
              [2, 23, 15],
              [3, 17, 9],
              [4, 18, 10],
              [5, 9, 5],
              [6, 11, 3],
              [7, 27, 19],
            ]}
            options={{
              hAxis: {
                title: "Time",
              },
              vAxis: {
                title: "Popularity",
              },
              series: {
                1: { curveType: "function" },
              },
            }}
            rootProps={{ "data-testid": "2" }}
          />
        </div>
      </div>
      <div className="cardContainer threeCols">
        <div className="card Right chart">
          {percentElement(
            PercentageElementStates.Increase,
            "Investments",
            15,
            "Week"
          )}
        </div>
        <div className="card Up chart">
          {percentElement(
            PercentageElementStates.Decrease,
            "Holdings",
            33,
            "Month"
          )}
        </div>
        <div className="card Left chart">
          <div className="headline two">Pie Chart</div>
          <Chart
            chartType="PieChart"
            data={[
              ["Task", "Hours per Day"],
              ["Work", 11],
              ["Eat", 2],
              ["Commute", 2],
              ["Watch TV", 2],
              ["Sleep", 7], // CSS-style declaration
            ]}
            options={{
              pieHole: 0.4,
              is3D: false,
            }}
          />
        </div>
      </div>
      <TutorialControl />
    </>
  );
};
export default EnterpriseTheme;
