import { useState } from "react";
import Icon from "../Icon/Icon";
import { Themes } from "../RenderComponent/RenderComponent";
import "./NavButton.scss";
export interface NavButtonOptions {
  icon?: string;
  label?: string;
}
const NavButton = ({
  theme,
  options,
}: {
  theme: Themes;
  options: NavButtonOptions;
}) => {
  const [isHover, setIsHover] = useState(false);
  return (
    <div
      className={" basicNavButton navButtonContainer" + Themes[theme]}
      onMouseEnter={() => setIsHover(true)}
      onMouseLeave={() => setIsHover(false)}
    >
      {isHover && theme == 1 && (
        <div className="burst">
          <div className="line">
            <Icon icon={options.icon} />
          </div>
          <div className="line">
            <Icon icon={options.icon} />
          </div>
          <div className="line">
            <Icon icon={options.icon} />
          </div>
          <div className="line">
            <Icon icon={options.icon} />
          </div>
          <div className="line">
            <Icon icon={options.icon} />
          </div>
          <div className="line">
            <Icon icon={options.icon} />
          </div>
        </div>
      )}
      <div className={"navButtonIcon"}>
        <Icon icon={options.icon} />
      </div>
      <div className={"navButtonLabel"}>{options.label}</div>
      <div className={"navButtonDropdownArrow"}>
        <Icon icon="ArrowDownward" />
      </div>
    </div>
  );
};
export default NavButton;
