import { Select } from '@material-ui/core';
import { useMemo } from 'react';
import { Themes } from '../../components/RenderComponent/RenderComponent';
import DefaultTheme from './DefaultTheme/DefaultTheme';
import EightiesTheme from './EightiesTheme/EightiesTheme';
import SafariTheme from './SafariTheme/SafariTheme';
import SpookyTheme from './SpookyTheme/SpookyTheme';
import './ThemeSwitcher.scss';
const ThemeSwitcher = ({theme, setTheme}:{theme: Themes,setTheme:any}) => {
    const handleTheme = (value: any) => {
        setTheme(value.target.selectedIndex);
    }
    const getPage = useMemo(()=>{
        switch(theme){
            case Themes.spooky:
                return <SpookyTheme setTheme={setTheme}/>
            case Themes.safari:
                return <SafariTheme setTheme={setTheme}/>
            case Themes.eighies:
                return <EightiesTheme setTheme={setTheme}/>
            default:
                return <DefaultTheme setTheme={setTheme}/>
        }
    },[theme])
    return(
        <div className='themeSwitcherContainer'>
            <Select
                native
                value={theme}
                onChange={handleTheme}
                >
                <option value={Themes.default}>Default</option>
                <option value={Themes.spooky}>Spooky</option>
                <option value={Themes.safari}>Safari</option>
                <option value={Themes.eighies}>Eighties</option>
                </Select>
            {getPage}
        </div>
    )
}
export default ThemeSwitcher;
