import { useState } from 'react';
import Icon from '../Icon/Icon';
import './NavButton.scss';
export interface NavButtonOptions {
    icon?: string, label?: string
}
const NavButton = ({options}:{options: NavButtonOptions}) => {
    const [isHover, setIsHover] = useState(false);
    return (
        <div className='navButtonContainer' onMouseEnter={()=>setIsHover(true)} onMouseLeave={()=>setIsHover(false)}>
            <Icon icon={options.icon}/>
            <div className='navButtonLabel'>
                {options.label}
            </div>
            {isHover && <div className='burst'>
                <div className='line'></div>
                <div className='line'></div>
            </div>}
        </div>
    )
}
export default NavButton;
