import { useEffect, useState } from "react";
import { getRandomKey } from "../pages/ThemeSwitcher/SalesTheme/SalesTheme";
import Icon from "./Icon/Icon";
interface iCart {
  cartItems: any[];
}
const CartComponent = (props: iCart) => {
  const [cartNum, setCartNum] = useState(0);
  const [animate, setAnimate] = useState(false);
  const [showCart, setShowCart] = useState<boolean>(false);
  useEffect(() => {
    const timeout = setTimeout(() => {
      setCartNum(props.cartItems.length);
      setAnimate(true);
      setTimeout(() => {
        setAnimate(false);
      }, 300);
    }, 1170);
    //return () => clearTimeout(timeout);
  }, [props.cartItems]);
  return (
    <>
      <div
        id="cart"
        className={"cart " + (animate ? " pop" : "")}
        onClick={() => setShowCart(!showCart)}
      >
        <span className="headline four">{cartNum ?? ""}</span>
        <Icon icon="Cart" />
      </div>
      {showCart && (
        <div className="cartDropdown">
          <div className="headline two">My Cart</div>
          {props.cartItems.length > 0 ? (
            props.cartItems.map((item, i) => {
              return (
                <div key={getRandomKey()} className="headline four">
                  {item.title}
                </div>
              );
            })
          ) : (
            <div className="headline four"></div>
          )}
        </div>
      )}
    </>
  );
};
export default CartComponent;
