import "./ResumePage.scss";
import "../ThemeSwitcher.scss";
import ThemeManager from "../../../ThemeManager";
import { Link } from "@material-ui/core";

export interface iDisplay {
  label: string;
  date?: string;
  image?: string;
  link?: string;
  context?: iDisplay[];
  isContext?: boolean;
}
const ResumePage = () => {
  const jobList: iDisplay[] = [
    {
      label: "StatusQuote Inc",
      date: "January 2020 - Present",
      context: [
        {
          label:
            "Engineered a React Native Investment app for an Enterprise level company within two months. Split logic codebase from render codebase, making a React and React Native Project that could start either.",
        },
        {
          label:
            "Exchanged communications with many top level clients, such as the Head of a Global Medical Company, to accurately create enterprise level in-house financial applications and organizational software.",
        },
        {
          label:
            "Created programs that organize financial data into Government Regulated Excel and PDF documents to be submitted easily. Simplifying a meticulous week long process, to only a click of a button.",
        },
        {
          label:
            "Worked on a multitude of projects ranging from React, Angular, .Net MVC, React Native, and many older in-house systems. I adapted quickly to new languages, new techniques and new enviornments to be successful.",
        },
      ],
    },
    {
      label: "GhoulCode",
      date: "November 2020 - Present",
      context: [
        {
          label:
            "Enhanced the user experience of the website, adding icons and increasing flow of the commerce page.",
        },
      ],
    },
  ];
  return (
    <>
      <div className="wholeBackground">
        <div className="contentBackground">
          <div className="content">
            <div
              className="titleText 
            titlePrimary"
            >
              Resume
            </div>
            <div className="jobContainer">
              <div className="primaryText titleOne slide1">Work History</div>
              {/*<div className="line" />*/}
              {jobList.map((job, i) => {
                return <DisplayElement options={job} />;
              })}
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
export default ResumePage;
export const DisplayElement = ({ options }: { options: iDisplay }) => {
  return (
    <div style={{ display: "flex", marginBottom: 24 }}>
      <div className="jobElementContainer ">
        <div
          className={
            options.isContext
              ? "teritaryText "
              : "secondaryText titleTwo slide2"
          }
        >
          {options.label}
          {options.link && (
            <a href={options.link}>
              <div
                style={{
                  display: "inline-block",
                  color: "var(--theme-button-success)",
                }}
                className="teritaryText"
              >
                - Link to Project
              </div>
            </a>
          )}
        </div>
        <div style={{ fontSize: 16 }} className="primaryText slide2">
          {options.date ?? ""}
        </div>
        {options.context && (
          <ul className={"jobContext "}>
            {options.context.map((job, i) => {
              job.isContext = true;
              return (
                <li
                  className="slide3"
                  style={{
                    animationDelay: (0.5 + 0.25 * (i + 1)).toString() + "s",
                  }}
                  key={i}
                >
                  <DisplayElement options={job} />
                </li>
              );
            })}
          </ul>
        )}
      </div>
      {options.image && <img className="jobImage slide2" src={options.image} />}
    </div>
  );
};
